import React, { useState, useEffect } from 'react'
import { Button, Icon } from 'antd'
import Router from 'next/router'
const Edit = ({user_id,article_id}) => {
    console.log('user_id',user_id)
    const [isAuth, setIsAuth] = useState(false)
    const editArticle = () =>{
        Router.push('/create?id='+article_id)
    }

    const deleteArticle = () => {
        console.log('asasd')
    }
    useEffect(()=>{

        if(user_id == localStorage.getItem('user_id')){
            setIsAuth(true)
        }else{
            setIsAuth(false)
        }
    },[])
    return (
        <>
            {isAuth &&<div><Button onClick={editArticle} type="dashed" icon="edit" style={{ textAlign: "right", marginRight: 10 }}>
                编辑
            </Button>

            <Button onClick={deleteArticle} type="dashed" icon="bank" style={{ textAlign: "right" }}>
                删除
            </Button></div>}
        </>
    )
}

export default Edit
