import React, { useState, useEffect, useContext } from 'react'
import Headers from '../components/Headers'
import Footers from '../components/Footers'
import Head from 'next/head'
import '../public/style/pages/index.css'
import '../public/style/pages/detail.css'
import { Layout, Row, Col, Icon, Affix, BackTop, Tag, Button } from 'antd';
import servicePath from '../config/apiUrl';
import User from '../components/User'
import axios from 'axios'
import marked from 'marked'
import hljs from "highlight.js";
import 'highlight.js/styles/monokai-sublime.css';
import 'markdown-navbar/dist/navbar.css';
import Tocify from '../components/tocify.tsx'
import Router from 'next/router'
import { UserContext } from '../components/UserRedux'

import { withRouter} from 'next/router'


const { Content } = Layout;
const Detail = (props) => {
    const renderer = new marked.Renderer();
    const tocify = new Tocify()
    const { user } = useContext(UserContext)
    console.log('user_id',global.user_id)
    renderer.heading = function (text, level, raw) {
        const anchor = tocify.add(text, level);
        return `<a id="${anchor}" href="#${anchor}" class="anchor-fix"><h${level}>${text}</h${level}></a>\n`;
    };


    marked.setOptions({
        renderer: renderer,
        gfm: true,
        pedantic: false,
        sanitize: false,
        tables: true,
        breaks: false,
        smartLists: true,
        smartypants: false,
        highlight: function (code) {
            return hljs.highlightAuto(code).value;
        }
    });
    console.log('props',props)
    let html = marked(props.content)

    const editArticle = () =>{
        Router.push('/create?id='+props.id)
    }

    const deleteArticle = () => {
        console.log('asasd')
    }


    useEffect(() => {

    })

    return (
        <Layout className="ly">
            <Head>
                <title>博人堂-{props.title}</title>
            </Head>
            <Headers />
            <Content className="content">
                <Row type="flex">
                    <Col xs={24} sm={24} md={24} lg={18} xl={18}>
                        <div className="comm-left">
                            <div className="detailed-title">
                                {props.title}
                            </div>

                            <div className="list-icon">
                                <span><Icon type="calendar" /> {props.created_at}</span>
                                <span><Icon type="folder" /> {props.category.category_name}</span>
                                <span><Icon type="fire" /> {props.watches}人</span>
                            </div>

                            <div className="detailed-content"
                                dangerouslySetInnerHTML={{ __html: html }}   >
                            </div>
                            <div className="tags" style={{display:"flex",justifyContent:"space-between",alignItems:"center",padding:'10px 20px'}}>
                                <div>
                                <Icon type="tags" theme="filled" style={{ marginRight: 12, fontSize: 16 }} />
                                {
                                    props.tags &&
                                    props.tags.map(tag => (
                                            <Tag key={tag} color="orange">{tag}</Tag>
                                    ))
                                }
                                </div>

                                {props&&props.user.id==global.user_id&& <div>
                                    <Button onClick={editArticle} type="dashed" icon="edit" style={{textAlign:"right",marginRight:10}}>
                                        编辑
                                    </Button>

                                    <Button onClick={deleteArticle} type="dashed" icon="bank" style={{textAlign:"right"}}>
                                        删除
                                    </Button>
                                </div>}
                            </div>
                        </div>
                    </Col>
                    <Col xs={0} sm={0} md={0} lg={0.5} xl={0.5}></Col>
                    <Col xs={0} sm={0} md={0} lg={5} xl={5} style={{ margin: '15px auto', }}>
                        <User user={props.user} />

                        {props.category.category_name == "教程" && <Affix style={{ marginTop: 20 }} offsetTop={0}>
                            <div className="detailed-nav comm-box1">
                                <div className="toc-list">
                                    {tocify && tocify.render()}
                                </div>
                            </div>
                        </Affix>}
                    </Col>
                </Row>
            </Content>
            <BackTop />
            <Footers />
        </Layout>
    )
}

Detail.getInitialProps = async (context) => {
    console.log('context',context)
    let id = context.ctx.query.id
    const promise = new Promise((resolve) => {
        axios(servicePath.getArticleDetail + id).then(
            (res) => {
                resolve(res.data.data)
            }
        )
    })

    return await promise
}



export default withRouter(Detail)
