import React, { useState } from 'react'
import { Layout, Row, Col, Menu } from 'antd';
import Link from 'next/link'
import axios from 'axios'
import servicePath from '../config/apiUrl';
const { Header } = Layout;

const Head = (props) => {
    console.log('props',props)
    const [current, setCurrent] = useState('')
    const [dataLoading, setDataLoading] = useState(true)
    const handleClick = e => {
        setCurrent(e.key)
        console.log('current: ', e)
    }
    return (
        <Header className="la">
            <Row type="flex">
                <Col xs={14} sm={15} md={18} lg={3} xl={3}>
                    <Link href="/">
                        <a>
                            <img className="logo" src={"../images/logo.png"} />
                        </a>
                    </Link>
                </Col>
                <Col className="memu-div" xs={0} sm={0} md={0} lg={9} xl={9}>
                    <Menu
                        className="menu"
                        theme="white"
                        mode="horizontal"
                        loading={dataLoading + ""}
                        onClick={handleClick}
                        selectedKeys={current}>
                        {/* {
                            props.map((item, index) => {
                                return <div key={index}>{item}</div>
                            })
                        } */}
                    </Menu>
                </Col>
                <Col xs={0} sm={0} md={0} lg={2} xl={2}>
                </Col>
                <Col className="text-right" xs={0} sm={0} md={0} lg={10} xl={10}>

                </Col>
            </Row>
        </Header>
    )
}

Head.getInitialProps = async (context) => {
    const promise = new Promise((resolve) => {
        axios({method:'get',url:servicePath.getCategories}).then(
            (res) => {
                console.log(res)
                resolve(res.data)
            }
        )
    })
    return await promise
}

export default Head
