import React, { useEffect, useState } from 'react';
import NoticeIcon from 'ant-design-pro/lib/NoticeIcon';
import { Row, Col, Menu, Icon, Button, Dropdown, Avatar, message } from 'antd';
import axios from "axios";
import servicePath from '../config/apiUrl';
import Router from 'next/router'

const NoticeView = () => {
    const [data, setData] = useState([])
    const [user, setUser] = useState()
    const [unreadCount, setUnreadCount] = useState()
    //获取会员数据
    const checkLogin = () => {
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();
        if (localStorage.getItem('token') != null) {
            axios({
                method: 'get',
                url: servicePath.getUserInfo,
                headers: {
                    'Authorization': 'Bearer ' + localStorage.getItem('token'),
                },
                withCredentials: false,
                cancelToken: source.token,
                transformResponse: function (data) {
                    if (data.indexOf('errors') != -1) {
                        data = JSON.parse(data);
                        for (var item in data.errors) {
                            message.error(data.errors[item][0]);
                        }
                    } else if (data.indexOf('Unauthenticated') != -1) {
                        localStorage.removeItem('token')
                        Router.push('/')
                    } else {
                        return data;
                    }
                }
            }).then(
                res => {
                    source.cancel('方法被取消');
                    setIsLoading(false)
                    res.data = eval('(' + res.data + ')');
                    setUser(res.data.data);
                    getNotification();
                    getNoread()
                }
            ).catch(function (error) {
                console.log(error)
            });
        } else {
            console.log('asasdas3')
        }
    }

    const getNotification = () => {
        axios({
            method: 'get',
            url: servicePath.getNotifications,
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('token'),
            },
            withCredentials: false,
        }).then(res => {
            console.log(res)
            setData(res.data.data);
        }
        )
    }

    const getNoticeData = (notices) => {
        if (notices.length === 0) {
            return {};
        }
        const newNotices = notices.map(notice => {
            const newNotice = { ...notice };
            // transform id to item key
            if (newNotice.id) {
                newNotice.key = newNotice.id;
            }
            if (newNotice.extra && newNotice.status) {
                const color = {
                    todo: '',
                    processing: 'blue',
                    urgent: 'red',
                    doing: 'gold',
                }[newNotice.status];
                newNotice.extra = (
                    <Tag color={color} style={{ marginRight: 0 }}>
                        {newNotice.extra}
                    </Tag>
                );
            }
            return newNotice;
        });
        return newNotices.reduce((pre, data) => {
            if (!pre[data.type]) {
                pre[data.type] = [];
            }
            pre[data.type].push(data);
            return pre;
        }, {});
    }

    const noticeData = getNoticeData(data);

    const onItemClick = (item, tabProps) => {
        console.log('item', item)
        axios({
            method: 'patch',
            url: servicePath.getNotificationStatus,
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('token'),
            },
            transformResponse: function (data) {
                console.log('article_link', data)
            }
        }).catch(function (error) {
            console.log(error)
        });
        Router.push(item.data['article_link']);
    }

    const getNoread = () => {
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();
        axios({
            method: 'get',
            url: servicePath.getNotificationStatus,
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('token'),
            },
            cancelToken: source.token,
            transformResponse: function (data) {
                source.cancel('方法被取消');
                data = eval('(' + data + ')');
                console.log('123', data)
                setUnreadCount(data.unread_count)
            }
        }).catch(function (error) {
            console.log(error)
        });
    }

    useEffect(() => {
        if (localStorage.getItem('token')) {
            checkLogin()
        } else {
            Router.push('/login')
        }
    },[])

    return (
        <>
            <NoticeIcon className="notice-icon" count={unreadCount} onItemClick={onItemClick} onClear={onClear}>
                <NoticeIcon.Tab
                    list={noticeData.message}
                    title="通知"
                    emptyText="您已读完所有消息"
                    emptyImage="https://gw.alipayobjects.com/zos/rmsportal/sAuJeJzSKbUmHfBQRzmZ.svg"
                />
                <NoticeIcon.Tab
                    list={noticeData.notification}
                    title="私信"
                    emptyText="你已查看所有通知"
                    emptyImage="https://gw.alipayobjects.com/zos/rmsportal/wAhyIChODzsoKIOBHcBk.svg"
                />
                <NoticeIcon.Tab
                    list={noticeData.event}
                    title="系统"
                    emptyText="你已完成所有待办"
                    emptyImage="https://gw.alipayobjects.com/zos/rmsportal/HsIsxMZiWKrNUavQUXqx.svg"
                />
            </NoticeIcon>
        </>
    )
}

export default NoticeView
