import { createStore, combineReducers, applyMiddleware } from 'redux'
import ReduxThunk from 'redux-thunk'

const initialState = {
    token: ''
}

const userInitialState = {
    user:''
}

function add(token) {
    return {
        type: ADD,
        token,
    }
}

function addAsync(token) {
    return (dispatch) => {
        setTimeout(()=>{
            dispatch(add(token))
        },1000)
    }
}


const ADD = 'ADD'
function tokenReducer(state = initialState, action){
    switch (action.type){
        case ADD:
            return { token: action.num || '' }
        default:
            return state
    }
}

const UPDATE_USER = 'UPDATE_USER'

function userReducer(state = userInitialState, action){
    switch (action.type){
        case UPDATE_USER:
            return {
                user:action.user
            }
        default:
            return state
    }
}





const allReducers = combineReducers({
    token:tokenReducer,
    user:userReducer
})

const store =createStore(
    allReducers, 
    {
    token: initialState,
    user: userInitialState
    },
    applyMiddleware(ReduxThunk),
)

