import React, { useState, memo, useContext } from 'react'
import { Upload, Icon, message, Modal } from 'antd'
import servicePath from '../config/apiUrl';
import { ModelContext } from '../pages/create'
const { Dragger } = Upload;

const Modals = () => {
    let {visible, setVisible, mdeValue, setMdeValue} = useContext(ModelContext)
    const handleCancel = e => {
        setVisible(false)
    };
    const pr = {
        uid: '-1',
        name: 'file',
        multiple: false,
        action: servicePath.uploadArticleImage,
        headers: {
            'Authorization': 'Bearer ' + localStorage.getItem('token'),
        },
        onChange(info) {
            console.log('info', info)
            const { status } = info.file;

            if (status !== 'uploading') {
                console.log(info.file, info.fileList);
            }
            if (status === 'done') {
                message.success(`${info.file.name} 上传成功.`);
                let valuefile = mdeValue + `![file](${info.file.response.file_path})`;
                console.log(valuefile)
                setMdeValue(valuefile)
                setVisible(false)
            } else if (status === 'error') {
                message.error(`${info.file.name} file upload failed.`);
            }
        },
    };


    return (
        <Modal
            footer={null}
            title="上传图片"
            visible={visible} onCancel={handleCancel}>

            <Dragger {...pr}>
                <p className="ant-upload-drag-icon">
                    <Icon type="inbox" />
                </p>
                <p className="ant-upload-text">点击或拖拽上传图片</p>
                <p className="ant-upload-hint">
                    支持单次或批量上传。支持图片格式文件
                    </p>
            </Dragger>
        </Modal>
    )
}

export default Modals
