import React from 'react'
import Head from 'next/head'
import ArticleList from '../components/ArticleList'
import TagList from '../components/TagList'
import LinkMe from '../components/LinkMe'
import Recommand from '../components/Recommand'
import { connect } from 'react-redux'
import {Row, Col, BackTop  } from 'antd';
import User from '../components/User';
import axios from 'axios'
const Home = (props) => {
  console.log('props', props)
  return(
  <div class="container">
    <Head>
      <title>博人堂--首页</title>
      <link rel="icon" href="/favicon.ico" />
    </Head>
    <Row type="flex" class="content">
          <Col xs={24} sm={24} md={24} lg={18} xl={18} style={{margin:16}}>
            <TagList  id={1}/>
            <ArticleList/>
          </Col>
          <Col xs={0} sm={0} md={0} lg={0.5} xl={0.5}></Col>
          <Col xs={0} sm={0} md={0} lg={5} xl={5}>
            <User user= {props} />
            <LinkMe />
            <Recommand />
          </Col>
    </Row>
    <BackTop />
  </div> 
)}

Home.getInitialProps = async () =>{
  const promise = new Promise((resolve)=>{
      axios(servicePath.getZhanInfo).then(
          (res)=>{
              console.log('user',res)
              resolve(res.data.data)
          }
      )
  })

  return await promise
}

export default connect(
  function mapStateToProps(state){
      axios(servicePath.getZhanInfo).then(
          (res)=>{
              state.user=res.data.data
          }
      )
    return {
      counter: state.counter.count,
      username: state.user.username
    }
  }
)(Home)
