import React, { useState, useEffect } from 'react'
import { Layout, Row, Col, Menu, Icon, Button, Dropdown, Avatar } from 'antd';
import NoticeIcon from 'ant-design-pro/lib/NoticeIcon';
import HeaderSearch from 'ant-design-pro/lib/HeaderSearch'
import Link from 'next/link'
import axios from 'axios'
import servicePath from '../config/apiUrl';
const { Header } = Layout;

const Head = (props) => {
    const [data, setData] = useState([])
    const [listData, setListData] = useState([]);
    const [unreadCount, setUnreadCount] = useState()
    const [current, setCurrent] = useState('')
    const [subkey, setSubkey] = useState()
    const [categories, setCategories] = useState([])
    const [dataLoading, setDataLoading] = useState(true)
    const [auth, setAuth] = useState()
    const handleClick = e => {
        setCurrent(e.key)
        console.log('current: ', e)
    }

    const onMenuClick = e => {
        if (e.key == 'logout') {
            axios({
                method: 'delete',
                url: servicePath.Logout,
                headers: {
                    'Authorization': 'Bearer ' + global.token,
                },
                withCredentials: false,
            }).then(
                res => {
                    global.token=null
                    window.location.href = "/login"
                }
            ).catch(function (error) {
                console.log(error)
            });

        }

        if (e.key == 'center') {
            window.location.href = "/account"
        }

        if (e.key == 'settings') {
            window.location.href = "/setting"
        }

    }

    const onClear = (tabTitle) => {
        axios({
            method: 'patch',
            url: servicePath.getNotificationStatus,
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('token'),
            },
            transformResponse: function (data) {
                console.log(data)
            }
        }).catch(function (error) {
            console.log(error)
        });
    }

    const getNoticeData = (notices) => {
        if (notices.length === 0) {
            return {};
        }
        const newNotices = notices.map(notice => {
            const newNotice = { ...notice };
            // transform id to item key
            if (newNotice.id) {
                newNotice.key = newNotice.id;
            }
            if (newNotice.extra && newNotice.status) {
                const color = {
                    todo: '',
                    processing: 'blue',
                    urgent: 'red',
                    doing: 'gold',
                }[newNotice.status];
                newNotice.extra = (
                    <Tag color={color} style={{ marginRight: 0 }}>
                        {newNotice.extra}
                    </Tag>
                );
            }
            return newNotice;
        });
        return newNotices.reduce((pre, data) => {
            if (!pre[data.type]) {
                pre[data.type] = [];
            }
            pre[data.type].push(data);
            return pre;
        }, {});
    }

    const getNoread = () => {
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();
        axios({
            method: 'get',
            url: servicePath.getNotificationStatus,
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('token'),
            },
            cancelToken: source.token,
            transformResponse: function (data) {
                source.cancel('方法被取消');
                data = eval('(' + data + ')');
                console.log('123', data)
                setUnreadCount(data.unread_count)
            }
        }).catch(function (error) {
            console.log(error)
        });
    }

    const noticeData = getNoticeData(data);

    const onItemClick = (item, tabProps) => {
        axios({
            method: 'patch',
            url: servicePath.getNotificationStatus,
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('token'),
            },
            transformResponse: function (data) {
                console.log(data)
            }
        }).catch(function (error) {
            console.log(error)
        });
        Router.push(item.data['article_link']);
    }

    const menu = (
        <Menu onClick={onMenuClick} selectedKeys={subkey}>
            <Menu.Item key="center">
                <Icon type="user" />
                <span>个人中心</span>
            </Menu.Item>
            <Menu.Item key="settings">
                <Icon type="setting" />
                <span>个人设置</span>
            </Menu.Item>
            <Menu.Divider />
            <Menu.Item key="logout">
                <Icon type="logout" />
                <span>退出登录</span>
            </Menu.Item>
        </Menu>
    );

    const getcategory = () => {
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();
        axios({
            method: 'get',
            url: servicePath.getCategories,
            cancelToken: source.token,
        }).then(
            res => {
                source.cancel('方法被取消');
                let str = [];
                for (let i = 0; i < res.data.length; i++) {
                    str.push(<Menu.Item key={res.data[i].icon}>
                        <Link href={res.data[i].href_value}>
                            <a>
                                <Icon type={res.data[i].icon} />
                                <span>{res.data[i].category_name}</span>
                            </a>
                        </Link>
                    </Menu.Item>)
                }
                setCategories(str)
                setDataLoading(false)
            }

        ).catch(function (error) {
            console.log(error)
        });
    }

    useEffect(() => {
        if (dataLoading) {
            getcategory();
        }
    }, [categories])
    return (
        <>
            <Header className="la">
                <Row type="flex">
                    <Col xs={14} sm={15} md={18} lg={3} xl={3}>
                        <Link href="/">
                            <a>
                                <img className="logo" src={"../images/logo.png"} />
                            </a>
                        </Link>
                    </Col>
                    <Col className="memu-div" xs={0} sm={0} md={0} lg={9} xl={9}>
                        <Menu
                            className="menu"
                            theme="white"
                            mode="horizontal"
                            loading={dataLoading + ""}
                            onClick={handleClick}
                            selectedKeys={current}>
                            {categories}
                        </Menu>
                    </Col>
                    <Col xs={0} sm={0} md={0} lg={2} xl={2}>
                    </Col>
                    <Col className="text-right" xs={0} sm={0} md={0} lg={10} xl={10}>
                        <Menu className="menu-right"
                            theme="dark"
                            mode="horizontal"
                            onClick={handleClick}
                            className="menu">
                            <Menu.Item>
                                <HeaderSearch
                                    placeholder="站内搜索"
                                    dataSource={listData}
                                    onSearch={value => {
                                        searchArticles(value)
                                    }}
                                    onPressEnter={value => {
                                        window.location.href = '/?q=' + encodeURI(value)
                                    }}
                                />
                            </Menu.Item>
                            <Menu.Item>
                                <NoticeIcon className="notice-icon" count={unreadCount} onItemClick={onItemClick} onClear={onClear}>
                                    <NoticeIcon.Tab
                                        list={noticeData.message}
                                        title="通知"
                                        emptyText="您已读完所有消息"
                                        emptyImage="https://gw.alipayobjects.com/zos/rmsportal/sAuJeJzSKbUmHfBQRzmZ.svg"
                                    />
                                    <NoticeIcon.Tab
                                        list={noticeData.notification}
                                        title="私信"
                                        emptyText="你已查看所有通知"
                                        emptyImage="https://gw.alipayobjects.com/zos/rmsportal/wAhyIChODzsoKIOBHcBk.svg"
                                    />
                                    <NoticeIcon.Tab
                                        list={noticeData.event}
                                        title="系统"
                                        emptyText="你已完成所有待办"
                                        emptyImage="https://gw.alipayobjects.com/zos/rmsportal/HsIsxMZiWKrNUavQUXqx.svg"
                                    />
                                </NoticeIcon>
                            </Menu.Item>
                            <Menu.Item key="login">
                                {auth && auth.id ? (
                                    <Dropdown overlay={menu} placement="bottomLeft">
                                        <div>
                                            <Avatar
                                                className="user"
                                                size="small"
                                                src={auth.avatar}
                                                alt="avatar"
                                            />
                                            <span className="authname" style={{ marginLeft: 10, color: '#000' }}>{auth.name}</span>
                                        </div>
                                    </Dropdown>
                                ) : (<Link href="/login">
                                    <a>
                                        <Button shape="round">登陆</Button>
                                    </a>
                                </Link>)
                                }
                            </Menu.Item>
                            <Menu.Item key="create">
                                <Link href="/create">
                                    <a>
                                        <Button shape="round">写文章</Button>
                                    </a>
                                </Link>
                            </Menu.Item>
                        </Menu>
                    </Col>
                </Row>
            </Header>
        </>
    )
}

