import React from 'react'
import Head from 'next/head'
import TeachList from '../components/TeachList'
import TagList from '../components/TagList'
import LinkMe from '../components/LinkMe'
import { connect } from 'react-redux'
import { Row, Col, BackTop } from 'antd';
import User from '../components/User';
import axios from 'axios'
import servicePath from '../config/apiUrl';

const Home = (props) => {
    console.log('props', props)
    return (
        <div className="container">
            <Head>
                <title>博人堂--教程</title>
                <link rel="icon" href="/favicon.ico" />
            </Head>
            <Row type="flex" className="content">
                <Col xs={24} sm={24} md={24} lg={18} xl={18} style={{ margin: 16 }}>
                    <TagList id={3} />
                    <TeachList />
                </Col>
                <Col xs={0} sm={0} md={0} lg={0.5} xl={0.5}></Col>
                <Col xs={0} sm={0} md={0} lg={5} xl={5}>
                    <User user={props.user} />
                    <LinkMe />
                </Col>
            </Row>
            <BackTop />
        </div>
    )
}


export default connect(
    function mapStateToProps(state) {
        axios(servicePath.getZhanInfo).then(
            (res) => {
                state.user = res.data.data
            }
        )
        return {
            user: state.user
        }
    }
)(Home)
