import React, {useEffect, useState} from 'react'
import {Layout, Menu, message, Row} from 'antd';
import BaseView from '../components/BaseView'
import Security from '../components/Security'
import axios from "axios";
import BindingView from "../components/BindingView";
const {  Content } = Layout;
const { Item } = Menu;

const Set = () => {
    const [user, setUser] = useState()
    const [isLoading, setIsLoading] = useState(true)
    const menuMap = {
        base: "基本设置",
        security: "安全设置",
        binding: "账号绑定",
        notification: "消息通知",
    };


    const mode = 'inline';

    const [selectKey, setSelectKey] = useState('base');

    const getRightTitle = () => {
        return menuMap[selectKey];
    };

    const renderChildren = () => {
        switch (selectKey) {
            case 'base':
                return <BaseView />;
            case 'security':
                return <Security/>;
            case 'binding':
                return <BindingView />;
            default:
                break;
        }
        return null;
    };

    const getMenu = () => {
        return Object.keys(menuMap).map(item => <Item key={item}>{menuMap[item]}</Item>);
    };


    return (
        <Content className="main" style={{ margin:'2rem auto'}}>
                <div className="leftMenu">
                    <Menu
                        mode={mode}
                        selectedKeys={[selectKey]}
                        onClick={({key}) => setSelectKey(key)}>
                        {getMenu()}
                    </Menu>
                </div>
                <div className="right">
                    <div className="title">{getRightTitle()}</div>
                    {renderChildren()}
                </div>
        </Content>
    )
}


export default Set
