import React, { useEffect, useState } from 'react';
import NoticeIcon from 'ant-design-pro/lib/NoticeIcon';
import { Row, Col, Menu, Icon, Button, Dropdown, Avatar, message } from 'antd';
import axios from "axios";
import servicePath from '../config/apiUrl';
import Router from 'next/router'

const NoticeView = () => {
    const [data, setData] = useState([])
    const [user, setUser] = useState()

    const onTabChange = (key) => {
        setTabKey(key)
        getList(props.id, key)
    };

    const operationTabList = [
        {
            key: 'notice',
            tab: (
                <span>
                    通知 <span style={{ fontSize: 14 }}>(8)</span>
                </span>
            ),
        },
        {
            key: 'system',
            tab: (
                <span>
                    系统 <span style={{ fontSize: 14 }}>(8)</span>
                </span>
            ),
        },
        {
            key: 'system',
            tab: (
                <span>
                    私信 <span style={{ fontSize: 14 }}>(8)</span>
                </span>
            ),
        }
    ];
    //获取会员数据
    const checkLogin = () => {
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();
        if (localStorage.getItem('token') != null) {
            axios({
                method: 'get',
                url: servicePath.getUserInfo,
                headers: {
                    'Authorization': 'Bearer ' + localStorage.getItem('token'),
                },
                withCredentials: false,
                cancelToken: source.token,
                transformResponse: function (data) {
                    if (data.indexOf('errors') != -1) {
                        data = JSON.parse(data);
                        for (var item in data.errors) {
                            message.error(data.errors[item][0]);
                        }
                    } else if (data.indexOf('Unauthenticated') != -1) {
                        localStorage.removeItem('token')
                        Router.push('/')
                    } else {
                        return data;
                    }
                }
            }).then(
                res => {
                    source.cancel('方法被取消');
                    setIsLoading(false)
                    res.data = eval('(' + res.data + ')');
                    setUser(res.data.data);
                    getNotification()
                }
            ).catch(function (error) {
                console.log(error)
            });
        } else {
            console.log('asasdas3')
        }
    }

    const getNotification = () => {
        axios({
            method: 'get',
            url: servicePath.getNotifications,
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('token'),
            },
            withCredentials: false,
        }).then(res => {
            console.log('data',res)
            setData(res.data.data);
        }
        )
    }







    useEffect(() => {
        if (localStorage.getItem('token')) {
            checkLogin()
        } else {
            Router.push('/login')
        }
    },[])

    return (
        <>

        </>
    )
}

export default NoticeView
