import React, { useState, useEffect } from 'react'
import { Layout, Row, Col, Menu, Icon } from 'antd';
import NoticeIcon from 'ant-design-pro/lib/NoticeIcon';
import Link from 'next/link'
import axios from 'axios'
import servicePath from '../config/apiUrl';
const { Header } = Layout;

const Head = (props) => {
    console.log('props', props)
    const [current, setCurrent] = useState('')
    const [categories, setCategories] = useState([])
    const [dataLoading, setDataLoading] = useState(true)
    const handleClick = e => {
        setCurrent(e.key)
        console.log('current: ', e)
    }

    const getcategory = () => {
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();
        axios({
            method: 'get',
            url: servicePath.getCategories,
            cancelToken: source.token,
        }).then(
            res => {
                source.cancel('方法被取消');
                let str = [];
                for (let i = 0; i < res.data.length; i++) {
                    str.push(<Menu.Item key={res.data[i].icon}>
                        <Link href={res.data[i].href_value}>
                            <a>
                                <Icon type={res.data[i].icon} />
                                <span>{res.data[i].category_name}</span>
                            </a>
                        </Link>
                    </Menu.Item>)
                }
                setCategories(str)
                setDataLoading(false)
            }

        ).catch(function (error) {
            console.log(error)
        });
    }

    useEffect(() => {
        if (dataLoading) {
            getcategory();
        }
    }, [])
    return (
        <>
            <Header className="la">
                <Row type="flex">
                    <Col xs={14} sm={15} md={18} lg={3} xl={3}>
                        <Link href="/">
                            <a>
                                <img className="logo" src={"../images/logo.png"} />
                            </a>
                        </Link>
                    </Col>
                    <Col className="memu-div" xs={0} sm={0} md={0} lg={9} xl={9}>
                        <Menu
                            className="menu"
                            theme="white"
                            mode="horizontal"
                            loading={dataLoading + ""}
                            onClick={handleClick}
                            selectedKeys={current}>
                            {categories}
                        </Menu>
                    </Col>
                    <Col xs={0} sm={0} md={0} lg={2} xl={2}>
                    </Col>
                    <Col className="text-right" xs={0} sm={0} md={0} lg={10} xl={10}>
                        <Menu className="menu-right"
                            theme="dark"
                            mode="horizontal"
                            onClick={handleClick}
                            className="menu">
                            <Menu.Item>
                                <Search />
                            </Menu.Item>
                            <Menu.Item>
                                <Notice />
                            </Menu.Item>
                            <Menu.Item key="login">
                                <UserRedux>
                                    <UserMenu />
                                </UserRedux>
                            </Menu.Item>
                            <Menu.Item key="create">
                                <Link href="/create">
                                    <a>
                                        <Button shape="round">写文章</Button>
                                    </a>
                                </Link>
                            </Menu.Item>
                        </Menu>
                    </Col>
                </Row>
            </Header>
        </>
    )
}

