import React, { useState, useEffect, memo, useMemo, useCallback } from 'react'
import { Layout, Row, Col, Menu, Icon, Button, Dropdown, Avatar, message } from 'antd';
import NoticeIcon from 'ant-design-pro/lib/NoticeIcon';
import HeaderSearch from 'ant-design-pro/lib/HeaderSearch'
import Link from 'next/link'
import Router from 'next/router'
import axios from 'axios'
import { connect } from 'react-redux'

import servicePath from '../config/apiUrl';

const { Header } = Layout;

const Head = (props) => {
    console.log('props', props.current)
    const [data, setData] = useState([])
    const [listData, setListData] = useState([]);
    const [unreadCount, setUnreadCount] = useState()
    const [current, setCurrent] = useState()
    const [subkey, setSubkey] = useState()
    const [categories, setCategories] = useState([])
    const [mcategories, setMcategories] = useState([])
    const [dataLoading, setDataLoading] = useState(true)
    const [auth, setAuth] = useState()
    const [collapsed, setCollapsed] = useState(false)
    const handleClick = e => {
        props.update_labels("")
        props.update_keywords("")
        setCurrent(e.key)
        props.update(e.key)
        console.log('current: ', e)
    }

    const toggleCollapsed = () => {
        setCollapsed(!collapsed)
    }

    const checkLogin = () => {
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();


        if (localStorage.getItem('token') != null) {
            axios({
                method: 'get',
                url: servicePath.getUserInfo,
                headers: {
                    'Authorization': 'Bearer ' + localStorage.getItem('token'),
                },
                withCredentials: false,
                cancelToken: source.token,
                transformResponse: function (data) {
                    console.log('user', data)
                    if (data.indexOf('errors') != -1) {
                        data = JSON.parse(data);
                        for (var item in data.errors) {
                            message.error(data.errors[item][0]);
                        }
                    } else if (data.indexOf('Unauthenticated') != -1) {
                        localStorage.removeItem('token')
                        localStorage.removeItem('user_id')
                        message.error('登录已过期，请重新登录');
                    } else {
                        return data;
                    }
                }
            }).then(
                res => {
                    source.cancel('方法被取消');
                    setDataLoading(false)
                    console.log('user', res.data)
                    res.data = eval('(' + res.data + ')');
                    setAuth(res.data.data);
                    localStorage.setItem('user_id', res.data.data.id)
                    getNotification()
                    getNoread()
                }
            ).catch(function (error) {
                console.log(error)
            });
        } else {
            console.log('asasdas2')
        }
    }

    const onMenuClick = e => {
        if (e.key == 'logout') {
            axios({
                method: 'delete',
                url: servicePath.Logout,
                headers: {
                    'Authorization': 'Bearer ' + localStorage.getItem('token'),
                },
                withCredentials: false,
            }).then(
                res => {
                    window.location.href = "/login"
                }
            ).catch(function (error) {
                console.log(error)
            });

        }

        if (e.key == 'center') {
            Router.push("/center?user_id=" + auth.id)
        }

        if (e.key == 'settings') {
            window.location.href = "/set"
        }

    }

    const getNotification = () => {
        axios({
            method: 'get',
            url: servicePath.getNotifications,
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('token'),
            },
            withCredentials: false,
        }).then(res => {
            console.log(res)
            setData(res.data.data);
        }
        )
    }

    const onClear = (tabTitle) => {
        axios({
            method: 'patch',
            url: servicePath.getNotificationStatus,
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('token'),
            },
            transformResponse: function (data) {
                console.log(data)
            }
        }).catch(function (error) {
            console.log(error)
        });
    }

    const getNoticeData = (notices) => {
        if (notices.length === 0) {
            return {};
        }
        const newNotices = notices.map(notice => {
            const newNotice = { ...notice };
            // transform id to item key
            if (newNotice.id) {
                newNotice.key = newNotice.id;
            }
            if (newNotice.extra && newNotice.status) {
                const color = {
                    todo: '',
                    processing: 'blue',
                    urgent: 'red',
                    doing: 'gold',
                }[newNotice.status];
                newNotice.extra = (
                    <Tag color={color} style={{ marginRight: 0 }}>
                        {newNotice.extra}
                    </Tag>
                );
            }
            return newNotice;
        });
        return newNotices.reduce((pre, data) => {
            if (!pre[data.type]) {
                pre[data.type] = [];
            }
            pre[data.type].push(data);
            return pre;
        }, {});
    }

    const getNoread = () => {
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();
        axios({
            method: 'get',
            url: servicePath.getNotificationStatus,
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('token'),
            },
            cancelToken: source.token,
            transformResponse: function (data) {
                source.cancel('方法被取消');
                data = eval('(' + data + ')');
                console.log('123', data)
                setUnreadCount(data.unread_count)
            }
        }).catch(function (error) {
            console.log(error)
        });
    }

    const noticeData = getNoticeData(data);

    const onItemClick = (item, tabProps) => {
        console.log('item', item)
        axios({
            method: 'patch',
            url: servicePath.getNotificationStatus,
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('token'),
            },
            transformResponse: function (data) {
                console.log('article_link', data)
            }
        }).catch(function (error) {
            console.log(error)
        });
        Router.push(item.data['article_link']);
    }

    const menu = (
        <Menu onClick={onMenuClick} selectedKeys={subkey}>
            <Menu.Item key="center">
                <Icon type="user" />
                <span>个人中心</span>
            </Menu.Item>
            <Menu.Item key="settings">
                <Icon type="setting" />
                <span>个人设置</span>
            </Menu.Item>
            <Menu.Divider />
            <Menu.Item key="logout">
                <Icon type="logout" />
                <span>退出登录</span>
            </Menu.Item>
        </Menu>
    );

    const mobilemenu = (
        <Menu
            className={collapsed ? 'show' : 'hidden'}
            theme="white"
            mode="inline"
            loading={dataLoading + ""}
            onClick={handleClick}
            selectedKeys={current}>
            {categories}
            {auth && auth.id ? (
                <Menu.Item key="center">
                    <Link href={{ pathname: '/center', query: { user_id: auth.id } }}>
                        <a>
                            <Icon type="user" />
                            <span>我的</span>
                        </a>
                    </Link>
                </Menu.Item>
            ) : (<Menu.Item key="login">
                <Link href="/login">
                    <a>
                        <Icon type="user" />
                        <span>登录</span>
                    </a>
                </Link>
            </Menu.Item>)}

            {auth && auth.id ? (
